import { useState, useEffect } from 'react';
import { Link } from 'react-scroll';
import axios from 'axios';
import 'animate.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './App.css';

function App() {
  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);
  const [contactForm, setContactForm] = useState({ name: '', email: '', message: '' });
  const [formStatus, setFormStatus] = useState('');
  const [activeSection, setActiveSection] = useState('');
  const [isResumeOpen, setIsResumeOpen] = useState(false);
  const [currentRole, setCurrentRole] = useState(0);
  const [selectedCertificate, setSelectedCertificate] = useState(null);

  const roles = ['Frontend Developer', 'Backend Developer', 'Full Stack Java Developer'];

  const certificates = [
    {
      id: 1,
      name: 'Cisco Cyber Security',
      image: '/Cisco Certificate.jpeg',
      description: 'In this course, I learned the fundamentals of cybersecurity, including network security, threat detection, and risk mitigation strategies. This certification enhances my ability to secure web applications and protect organizational data, making me a valuable asset in developing secure software solutions.'
    },
    {
      id: 2,
      name: 'Android App Development',
      image: '/Android_App_Development.jpg',
      description: 'This course covered Android app development using Java and Android Studio, focusing on building user interfaces, managing app lifecycles, and integrating APIs. These skills enable me to create robust mobile applications, expanding my expertise in cross-platform development.'
    },
    {
      id: 3,
      name: 'Frontend Development',
      image: '/IBM.jpg',
      description: 'Through IBM’s frontend development course, I gained proficiency in HTML, CSS, JavaScript, and React.js, mastering responsive web design and dynamic user interfaces. This certification strengthens my ability to build engaging and user-friendly web applications, critical for modern software development.'
    },
    {
      id: 4,
      name: 'Java',
      image: '/Java Certificate.png',
      description: 'This course deepened my knowledge of Java programming, covering core and advanced concepts like object-oriented programming, multithreading, and Spring Boot. These skills are essential for building scalable backend systems, enhancing my versatility as a full-stack developer.'
    },
    {
      id: 5,
      name: 'Full Stack Java Developer',
      image: '/Java Certificate.png',
      description: 'As a Full Stack Java Developer, I’ve learned to build complete web applications using technologies like React.js, Core Java, Spring Boot, and MySQL. On the frontend, I used HTML, CSS, JavaScript, and React to design responsive UIs, while the backend involved building RESTful APIs using Spring Boot, Spring Security, and JPA. I worked with databases using MySQL and Hibernate, and implemented secure authentication with JWT. I also gained hands-on experience with tools like Git, Maven, Docker, and Postman, and deployed projects on platforms like Heroku and Vercel. Real-time projects like an E-Commerce site, Fitness Tracker, and Leave Management System helped build a strong portfolio, making me job-ready for roles like Java Developer, Backend Developer, or Full Stack Developer across various industries.'
    }
  ];

  // Project images in order
  const projectImages = [
    '/Weather.webp',
    '/Employee.png',
    '/olivaclinic_logo.jpg',
    '/car.png',
    '/Realestate.jpg'
  ];

  useEffect(() => {
    axios.get('http://localhost:5000/api/skills').then(res => setSkills(res.data));
    axios.get('http://localhost:5000/api/projects').then(res => setProjects(res.data));

    // Role cycling animation
    const roleInterval = setInterval(() => {
      setCurrentRole((prev) => (prev + 1) % roles.length);
    }, 2000);

    return () => clearInterval(roleInterval);
  }, [roles.length]);

  const handleContactSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/contact', contactForm);
      setFormStatus('Message sent successfully!');
      setContactForm({ name: '', email: '', message: '' });
    } catch (error) {
      setFormStatus('Failed to send message.');
    }
  };

  const toggleResumeModal = () => {
    setIsResumeOpen(!isResumeOpen);
  };

  const openCertificateModal = (certificate) => {
    setSelectedCertificate(certificate);
  };

  const closeCertificateModal = () => {
    setSelectedCertificate(null);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-100 via-blue-100 to-pink-100 animate__animated animate__fadeIn">
      {/* Navbar */}
      <nav className="bg-gradient-to-r from-purple-800 via-blue-800 to-pink-700 text-white p-4 fixed w-full top-0 z-20 shadow-2xl animate__animated animate__fadeInDown">
        <div className="container mx-auto flex justify-between items-center">
          <div className="text-4xl font-black tracking-tight animate__animated animate__bounceIn">
            Panga Syamsundar Rao
          </div>
          <div className="flex items-center space-x-4">
            {['About', 'Skills', 'Projects', 'Certificates', 'Experience', 'Education', 'Contact'].map((item, idx) => (
              <Link
                key={item}
                to={item.toLowerCase()}
                smooth={true}
                duration={500}
                spy={true}
                activeClass="text-yellow-300 border-b-2 border-yellow-300"
                onSetActive={() => setActiveSection(item.toLowerCase())}
                className={`cursor-pointer hover:text-yellow-300 transition-colors duration-300 font-semibold text-lg animate__animated animate__fadeInDown animate__delay-${idx + 1}s`}
              >
                {item}
              </Link>
            ))}
            <i
              className="fas fa-user-circle text-4xl cursor-pointer hover:scale-110 transition-all duration-300 animate__animated animate__zoomIn"
              onClick={toggleResumeModal}
            />
          </div>
        </div>
      </nav>

      {/* Resume Modal */}
      {isResumeOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 animate__animated animate__fadeIn">
          <div className="bg-white p-8 rounded-2xl max-w-3xl w-full max-h-[80vh] overflow-y-auto shadow-2xl animate__animated animate__zoomIn">
            <h2 className="text-3xl font-bold text-black mb-6">Resume</h2>
            {/* ...resume content unchanged... */}
            <button
              onClick={toggleResumeModal}
              className="mt-6 bg-gradient-to-r from-purple-700 to-blue-700 text-white p-3 rounded-lg hover:from-blue-700 hover:to-purple-700 transition-all duration-300 w-full font-bold"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Certificate Modal */}
      {selectedCertificate && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 animate__animated animate__fadeIn">
          <div className="bg-white p-8 rounded-2xl max-w-3xl w-full max-h-[80vh] overflow-y-auto shadow-2xl animate__animated animate__zoomIn">
            <h2 className="text-3xl font-bold text-black mb-6">{selectedCertificate.name}</h2>
            <img
              src={selectedCertificate.image}
              alt={selectedCertificate.name}
              className="w-full h-auto object-contain rounded-lg mb-6"
            />
            <button
              onClick={closeCertificateModal}
              className="bg-gradient-to-r from-purple-700 to-blue-700 text-white p-3 rounded-lg hover:from-blue-700 hover:to-purple-700 transition-all duration-300 w-full font-bold"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* About */}
      <section id="about" className="min-h-screen flex items-center justify-center bg-gradient-to-br from-white via-purple-50 to-blue-50 relative overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-br from-purple-200 via-blue-200 to-pink-200 opacity-20 animate__animated animate__pulse animate__infinite animate__slower"></div>
        <div className="container mx-auto flex flex-col md:flex-row items-center justify-between px-6 z-10 animate__animated animate__zoomIn">
          <div className="md:w-2/3 text-center md:text-left mb-12 md:mb-0">
            <h1 className="text-6xl font-black mb-6 text-purple-900 animate__animated animate__fadeInUp">
              Welcome to My Portfolio
            </h1>
            <p className="text-xl leading-relaxed text-gray-800 animate__animated animate__fadeInUp animate__delay-1s">
              I am a well-organized, energetic, and goal-oriented fresher, possessing positive attitude, problem-solving,
              and leadership skills with flair to explore suitable avenues in software development with good knowledge in
              Java, frontend development, C, and Oracle. To work in an organization that provides me with ample
              opportunities to enhance my skills and knowledge along with contributing to the growth of the organization.
            </p>
            <div className="mt-6 text-2xl font-semibold text-purple-800 animate__animated animate__fadeIn animate__delay-2s role-animation">
              {roles[currentRole]}
            </div>
          </div>
          <div className="md:w-1/3 flex justify-center">
            <img
              src="/syam.webp"
              alt="Panga Syamsundar Rao"
              className="w-80 h-80 rounded-full shadow-2xl object-cover border-4 border-purple-400 transform hover:scale-110 hover:rotate-6 transition-all duration-500 animate__animated animate__zoomInDown animate-border"
            />
          </div>
        </div>
      </section>

      {/* Skills */}
      <section id="skills" className={`py-20 bg-gradient-to-br from-white via-blue-50 to-purple-50 ${activeSection === 'skills' ? 'animate__animated animate__lightSpeedInRight' : ''}`}>
        <div className="container mx-auto">
          <h2 className="text-5xl font-black text-center mb-10 text-purple-900">Skills</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
            {skills.map(skill => (
              <div
                key={skill.id}
                className="p-6 bg-gradient-to-br from-purple-100 to-blue-100 rounded-2xl shadow-neumorphic hover:shadow-glow hover:-translate-y-2 transition-all duration-300 text-center font-semibold text-lg text-purple-900 animate__animated animate__bounceIn"
              >
                {skill.name}
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Projects */}
      <section id="projects" className={`py-20 bg-gradient-to-br from-purple-900 via-blue-900 to-pink-900 text-white ${activeSection === 'projects' ? 'animate__animated animate__flipInX' : ''}`}>
        <div className="container mx-auto">
          <h2 className="text-5xl font-black text-center mb-10 text-yellow-300 drop-shadow-lg">Projects</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {projects.map((project, idx) => (
              <div
                key={project.id}
                className="p-6 bg-gradient-to-br from-purple-700 via-blue-800 to-pink-700 rounded-2xl shadow-neumorphic hover:shadow-glow hover:-translate-y-2 transition-all duration-300 animate__animated animate__fadeInUp border-2 border-transparent hover:border-yellow-300"
              >
                <h3 className="text-2xl font-bold text-yellow-300 mb-2">{project.title}</h3>
                <img
                  src={projectImages[idx]}
                  alt={project.title}
