import { useState, useEffect } from 'react';
import { Link } from 'react-scroll';
import axios from 'axios';
import { motion, AnimatePresence } from 'framer-motion';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './App.css';

function App() {
  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);
  const [contactForm, setContactForm] = useState({ name: '', email: '', message: '' });
  const [formStatus, setFormStatus] = useState('');
  const [activeSection, setActiveSection] = useState('');
  const [isResumeOpen, setIsResumeOpen] = useState(false);
  const [currentRole, setCurrentRole] = useState(0);
  const [selectedCertificate, setSelectedCertificate] = useState(null);

  const roles = ['Frontend Developer', 'Backend Developer', 'Full Stack Java Developer'];

  const certificates = [
    {
      id: 1,
      name: 'Cisco Cyber Security',
      image: '/Cisco Certificate.jpeg',
      description: 'In this course, I learned the fundamentals of cybersecurity, including network security, threat detection, and risk mitigation strategies. This certification enhances my ability to secure web applications and protect organizational data, making me a valuable asset in developing secure software solutions.'
    },
    {
      id: 2,
      name: 'Android App Development',
      image: '/Android_App_Development.jpg',
      description: 'This course covered Android app development using Java and Android Studio, focusing on building user interfaces, managing app lifecycles, and integrating APIs. These skills enable me to create robust mobile applications, expanding my expertise in cross-platform development.'
    },
    {
      id: 3,
      name: 'Frontend Development',
      image: '/IBM.jpg',
      description: 'Through IBM’s frontend development course, I gained proficiency in HTML, CSS, JavaScript, and React.js, mastering responsive web design and dynamic user interfaces. This certification strengthens my ability to build engaging and user-friendly web applications, critical for modern software development.'
    },
    {
      id: 4,
      name: 'Java',
      image: '/Java Certificate.png',
      description: 'This course deepened my knowledge of Java programming, covering core and advanced concepts like object-oriented programming, multithreading, and Spring Boot. These skills are essential for building scalable backend systems, enhancing my versatility as a full-stack developer.'
    },
    {
      id: 5,
      name: 'Full Stack Java Developer',
      image: '/Java Certificate.png',
      description: 'As a Full Stack Java Developer, I’ve learned to build complete web applications using technologies like React.js, Core Java, Spring Boot, and MySQL. On the frontend, I used HTML, CSS, JavaScript, and React to design responsive UIs, while the backend involved building RESTful APIs using Spring Boot, Spring Security, and JPA. I worked with databases using MySQL and Hibernate, and implemented secure authentication with JWT. I also gained hands-on experience with tools like Git, Maven, Docker, and Postman, and deployed projects on platforms like Heroku and Vercel. Real-time projects like an E-Commerce site, Fitness Tracker, and Leave Management System helped build a strong portfolio, making me job-ready for roles like Java Developer, Backend Developer, or Full Stack Developer across various industries.'
    }
  ];

  const projectImages = [
    '/Weather.webp',
    '/Employee.png',
    '/olivaclinic_logo.jpg',
    '/car.png',
    '/Realestate.jpg'
  ];

  useEffect(() => {
    axios.get('http://localhost:5000/api/skills').then(res => setSkills(res.data));
    axios.get('http://localhost:5000/api/projects').then(res => setProjects(res.data));

    const roleInterval = setInterval(() => {
      setCurrentRole((prev) => (prev + 1) % roles.length);
    }, 2000);

    return () => clearInterval(roleInterval);
  }, [roles.length]);

  const handleContactSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/contact', contactForm);
      setFormStatus('Message sent successfully!');
      setContactForm({ name: '', email: '', message: '' });
    } catch (error) {
      setFormStatus('Failed to send message.');
    }
  };

  const toggleResumeModal = () => {
    setIsResumeOpen(!isResumeOpen);
  };

  const openCertificateModal = (certificate) => {
    setSelectedCertificate(certificate);
  };

  const closeCertificateModal = () => {
    setSelectedCertificate(null);
  };

  // Animation variants
  const fadeIn = {
    hidden: { opacity: 0, y: 50 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.8, ease: 'easeOut' } }
  };

  const scaleIn = {
    hidden: { opacity: 0, scale: 0.8 },
    visible: { opacity: 1, scale: 1, transition: { duration: 0.5, ease: 'easeOut' } }
  };

  const flipIn = {
    hidden: { opacity: 0, rotateY: 90 },
    visible: { opacity: 1, rotateY: 0, transition: { duration: 0.6, ease: 'easeOut' } }
  };

  const roleAnimation = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
    exit: { opacity: 0, y: -20, transition: { duration: 0.3 } }
  };

  const modalZoom = {
    hidden: { opacity: 0, scale: 0.7 },
    visible: { opacity: 1, scale: 1, transition: { duration: 0.5, type: 'spring', bounce: 0.4 } }
  };

  return (
    <div className="min-h-screen bg-black text-white overflow-x-hidden font-inter">
      {/* Navbar */}
      <motion.nav
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ duration: 0.6, ease: 'easeOut' }}
        className="bg-gray-900 bg-opacity-90 backdrop-blur-lg text-white p-4 fixed w-full top-0 z-20 shadow-lg border-b border-blue-500"
      >
        <div className="container mx-auto flex justify-between items-center">
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.2 }}
            className="text-3xl font-bold tracking-tight cursor-pointer hover:text-blue-400 transition-colors duration-300"
            onClick={toggleResumeModal}
          >
            Panga Syamsundar Rao
          </motion.div>
          <div className="flex items-center space-x-6">
            {['About', 'Skills', 'Projects', 'Certificates', 'Experience', 'Education', 'Contact'].map((item, idx) => (
              <Link
                key={item}
                to={item.toLowerCase()}
                smooth={true}
                duration={500}
                spy={true}
                activeClass="text-blue-400 border-b-2 border-blue-400"
                onSetActive={() => setActiveSection(item.toLowerCase())}
                className="cursor-pointer hover:text-blue-400 transition-colors duration-300 font-medium text-lg"
              >
                <motion.div
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.3 + idx * 0.1 }}
                >
                  {item}
                </motion.div>
              </Link>
            ))}
            <motion.i
              whileHover={{ scale: 1.2, rotate: 360 }}
              className="fas fa-user-circle text-3xl cursor-pointer hover:text-blue-400"
              onClick={toggleResumeModal}
            />
          </div>
        </div>
      </motion.nav>

      {/* Resume Modal */}
      <AnimatePresence>
        {isResumeOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50"
          >
            <motion.div
              variants={modalZoom}
              initial="hidden"
              animate="visible"
              exit="hidden"
              className="bg-gray-800 bg-opacity-90 backdrop-blur-lg p-8 rounded-xl max-w-4xl w-full max-h-[85vh] overflow-y-auto shadow-2xl border border-blue-500"
            >
              <h2 className="text-3xl font-bold text-blue-400 mb-6">Resume</h2>
              <div className="text-gray-200">
                <h3 className="text-2xl font-semibold mb-2">Panga Syamsundar Rao</h3>
                <p>Phone: +91-8919004890 | Email: syampanga2003@gmail.com</p>
                <h4 className="text-xl font-semibold mt-4 mb-2 text-blue-400">Career Objective</h4>
                <p className="mb-4">
                  I am a well-organized, energetic, and goal-oriented fresher, possessing a positive attitude, problem-solving, and leadership skills with a flair to explore suitable avenues in software development with good knowledge in Java, frontend development, C, and Oracle. To work in an organization that provides me with ample opportunities to enhance my skills and knowledge while contributing to the growth of the organization.
                </p>
                <h4 className="text-xl font-semibold mt-4 mb-2 text-blue-400">Core Competencies</h4>
                <ul className="grid grid-cols-2 gap-2 mb-4">
                  {['HTML and CSS', 'JavaScript', 'Core Java', 'Advance Java', 'Oracle SQL/PL', 'Spring Boot & Microservices', 'React', 'C Language', 'Cyber Security', 'Software Development Lifecycle', 'Waterfall & Agile Methodologies', 'Software Testing'].map(skill => (
                    <motion.li
                      key={skill}
                      variants={fadeIn}
                      initial="hidden"
                      animate="visible"
                      className="list-disc ml-5"
                    >
                      {skill}
                    </motion.li>
                  ))}
                </ul>
                <h4 className="text-xl font-semibold mt-4 mb-2 text-blue-400">Academic Qualification</h4>
                <table className="w-full border-collapse mb-4 text-gray-200">
                  <thead>
                    <tr className="bg-gray-700">
                      <th className="border border-gray-600 p-2">Degree/Examination</th>
                      <th className="border border-gray-600 p-2">School/Institute Name</th>
                      <th className="border border-gray-600 p-2">Year of Passing</th>
                      <th className="border border-gray-600 p-2">CGPA/Percentage</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td className="border border-gray-600 p-2">B.Tech (Computer Science & Engineering)</td>
                      <td className="border border-gray-600 p-2">CT University, Punjab</td>
                      <td className="border border-gray-600 p-2">2020</td>
                      <td className="border border-gray-600 p-2">7.2</td>
                    </tr>
                    <tr>
                      <td className="border border-gray-600 p-2">Intermediate (M.P.C)</td>
                      <td className="border border-gray-600 p-2">Sri Chaitanya Junior College, Visakhapatnam</td>
                      <td className="border border-gray-600 p-2">2018</td>
                      <td className="border border-gray-600 p-2">8.7</td>
                    </tr>
                    <tr>
                      <td className="border border-gray-600 p-2">S.S.C</td>
                      <td className="border border-gray-600 p-2">Bradlaugh School</td>
                      <td className="border border-gray-600 p-2">2018</td>
                      <td className="border border-gray-600 p-2">9.2</td>
                    </tr>
                  </tbody>
                </table>
                <h4 className="text-xl font-semibold mt-4 mb-2 text-blue-400">Internships</h4>
                <ul className="list-disc pl-5 mb-4">
                  <li>Successfully completed Cisco training on Cyber Security.</li>
                  <li>Successfully completed Internship on frontend development in IBM (Online Course).</li>
                  <li>Doing Full stack Java developer course in Naresh I Technologies in Hyderabad.</li>
                </ul>
                <h4 className="text-xl font-semibold mt-4 mb-2 text-blue-400">Trainings Attended</h4>
                <ul className="list-disc pl-5 mb-4">
                  <li>Successfully completed C/HTML/CSS/JavaScript training program at Naresh I Technologies, Hyderabad.</li>
                  <li>Successfully completed Cisco training on Cyber Security.</li>
                  <li>Successfully completed Java training in Internshala.</li>
                </ul>
                <h4 className="text-xl font-semibold mt-4 mb-2 text-blue-400">Projects</h4>
                <ul className="list-disc pl-5 mb-4">
                  <li>
                    <strong>Weather Report Application</strong>: Developed a full stack web application that provides real-time weather updates using Java Spring Boot for the backend and React for the frontend. Integrated with Open Weather Map API to fetch and display current weather conditions and forecasts.
                  </li>
                  <li>
                    <strong>Employee Leave Management System</strong>: Developed a comprehensive leave management system using Java for the backend and HTML, CSS, and JavaScript for the frontend, with Oracle as the database. Enabled efficient tracking, approval workflows, and reporting of employee leave data.
                  </li>
                  <li>
                    <strong>Oliva Skin & Care</strong>: A React-based app offering personalized skincare routines, a catalogue of olive-based products, and expert skincare tips. <a href="http://invroiceolivaclinic.com/" target="_blank" rel="noopener noreferrer" className="text-blue-400 hover:underline">Link</a>
                  </li>
                  <li>
                    <strong>Carbia Motors</strong>: A frontend React app showcasing car listings, specifications, and dealer information. <a href="https://carbook-rho.vercel.app/main" target="_blank" rel="noopener noreferrer" className="text-blue-400 hover:underline">Link</a>
                  </li>
                  <li>
                    <strong>Real Estate Explorer</strong>: A React-based front-end application for browsing and filtering property listings. <a href="https://frontend-virid-one-37.vercel.app/login" target="_blank" rel="noopener noreferrer" className="text-blue-400 hover:underline">Link</a>
                  </li>
                </ul>
                <h4 className="text-xl font-semibold mt-4 mb-2 text-blue-400">Personal Details</h4>
                <p>Languages Known: English, Telugu, and Hindi</p>
                <h4 className="text-xl font-semibold mt-4 mb-2 text-blue-400">Declaration</h4>
                <p>I hereby declare that the above written particulars are true to the best of my knowledge and belief.</p>
                <p className="mt-2">Syamsundar</p>
              </div>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={toggleResumeModal}
                className="mt-6 bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600 transition-all duration-300 w-full font-bold"
              >
                Close
              </motion.button>
