import { useState, useEffect } from 'react';
import { Link } from 'react-scroll';
import axios from 'axios';
import 'animate.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './App.css';

function App() {
  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);
  const [contactForm, setContactForm] = useState({ name: '', email: '', message: '' });
  const [formStatus, setFormStatus] = useState('');
  const [activeSection, setActiveSection] = useState('');
  const [isResumeOpen, setIsResumeOpen] = useState(false);
  const [currentRole, setCurrentRole] = useState(0);
  const [selectedCertificate, setSelectedCertificate] = useState(null);

  const roles = ['Frontend Developer', 'Backend Developer', 'Full Stack Java Developer'];

  const certificates = [
    {
      id: 1,
      name: 'Cisco Cyber Security',
      image: '/Cisco Certificate.jpeg',
      description: 'In this course, I learned the fundamentals of cybersecurity, including network security, threat detection, and risk mitigation strategies. This certification enhances my ability to secure web applications and protect organizational data, making me a valuable asset in developing secure software solutions.'
    },
    {
      id: 2,
      name: 'Android App Development',
      image: '/Android_App_Development.jpg',
      description: 'This course covered Android app development using Java and Android Studio, focusing on building user interfaces, managing app lifecycles, and integrating APIs. These skills enable me to create robust mobile applications, expanding my expertise in cross-platform development.'
    },
    {
      id: 3,
      name: 'Frontend Development',
      image: '/IBM.jpg',
      description: 'Through IBMâ€™s frontend development course, I gained proficiency in HTML, CSS, JavaScript, and React.js, mastering responsive web design and dynamic user interfaces. This certification strengthens my ability to build engaging and user-friendly web applications, critical for modern software development.'
    },
    {
      id: 4,
      name: 'Java',
      image: '/Java Certificate.png',
      description: 'This course deepened my knowledge of Java programming, covering core and advanced concepts like object-oriented programming, multithreading, and Spring Boot. These skills are essential for building scalable backend systems, enhancing my versatility as a full-stack developer.'
    }
  ];

  useEffect(() => {
    axios.get('http://localhost:5000/api/skills').then(res => setSkills(res.data));
    axios.get('http://localhost:5000/api/projects').then(res => setProjects(res.data));

    // Role cycling animation
    const roleInterval = setInterval(() => {
      setCurrentRole((prev) => (prev + 1) % roles.length);
    }, 2000);

    return () => clearInterval(roleInterval);
  }, []);

  const handleContactSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/contact', contactForm);
      setFormStatus('Message sent successfully!');
      setContactForm({ name: '', email: '', message: '' });
    } catch (error) {
      setFormStatus('Failed to send message.');
    }
  };

  const toggleResumeModal = () => {
    setIsResumeOpen(!isResumeOpen);
  };

  const openCertificateModal = (certificate) => {
    setSelectedCertificate(certificate);
  };

  const closeCertificateModal = () => {
    setSelectedCertificate(null);
  };

  return (
    <div className="min-h-screen bg-white animate__animated animate__fadeIn">
      {/* Navbar */}
      <nav className="bg-black bg-opacity-90 text-white p-4 fixed w-full top-0 z-20 shadow-2xl animate__animated animate__fadeInDown">
        <div className="container mx-auto flex justify-between items-center">
          <div className="text-4xl font-black tracking-tight animate__animated animate__bounceIn">
            Panga Syamsundar Rao
          </div>
          <div className="flex items-center space-x-4">
            {['Home', 'Skills', 'Projects', 'Certificates', 'Experience', 'Education', 'Contact'].map((item, idx) => (
              <Link
                key={item}
                to={item.toLowerCase()}
                smooth={true}
                duration={500}
                spy={true}
                activeClass="text-gray-300 border-b-2 border-gray-300"
                onSetActive={() => setActiveSection(item.toLowerCase())}
                className={`cursor-pointer hover:text-gray-300 transition-colors duration-300 font-semibold text-lg animate__animated animate__fadeInDown animate__delay-${idx + 1}s`}
              >
                {item}
              </Link>
            ))}
            <i
              className="fas fa-user-circle text-4xl cursor-pointer hover:scale-110 transition-all duration-300 animate__animated animate__zoomIn"
              onClick={toggleResumeModal}
            />
          </div>
        </div>
      </nav>

      {/* Resume Modal */}
      {isResumeOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 animate__animated animate__fadeIn">
          <div className="bg-white p-8 rounded-2xl max-w-3xl w-full max-h-[80vh] overflow-y-auto shadow-2xl animate__animated animate__zoomIn">
            <h2 className="text-3xl font-bold text-black mb-6">Resume</h2>
            <div className="text-black">
              <h3 className="text-2xl font-semibold mb-2">Panga Syamsundar Rao</h3>
              <p>Phone: +91-8919004890 | Email: syampanga2003@gmail.com</p>
              <h4 className="text-xl font-semibold mt-4 mb-2">Career Objective</h4>
              <p className="mb-4">
                I am a well-organized, energetic and goal-oriented fresher, possessing positive attitude, problem-solving and leadership skills with flair to explore suitable avenues in software development with good knowledge in Java, frontend development, C and Oracle. To work in an organization that provides me with ample opportunities to enhance my skills and knowledge along with contributing to the growth of the organization.
              </p>
              <h4 className="text-xl font-semibold mt-4 mb-2">Core Competencies</h4>
              <ul className="list-disc pl-5 mb-4">
                {['HTML and CSS', 'JavaScript', 'Core Java', 'Advance Java', 'Oracle SQL/PL', 'Spring Boot & Microservices', 'React', 'C Language', 'Cyber Security', 'Software Development Lifecycle', 'Waterfall & Agile Methodologies', 'Software Testing'].map(skill => (
                  <li key={skill}>{skill}</li>
                ))}
              </ul>
              <h4 className="text-xl font-semibold mt-4 mb-2">Academic Qualification</h4>
              <table className="w-full border-collapse mb-4">
                <thead>
                  <tr className="bg-gray-200">
                    <th className="border p-2">Degree/Examination</th>
                    <th className="border p-2">School/Institute Name</th>
                    <th className="border p-2">Year of Passing</th>
                    <th className="border p-2">CGPA/Percentage</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td className="border p-2">B.Tech (Computer Science & Engineering)</td>
                    <td className="border p-2">CT University, Punjab</td>
                    <td className="border p-2">2020</td>
                    <td className="border p-2">7.2</td>
                  </tr>
                  <tr>
                    <td className="border p-2">Intermediate (M.P.C)</td>
                    <td className="border p-2">Sri Chaitanya Junior College, Visakhapatnam</td>
                    <td className="border p-2">2018</td>
                    <td className="border p-2">8.7</td>
                  </tr>
                  <tr>
                    <td className="border p-2">S.S.C</td>
                    <td className="border p-2">Bradlaugh School</td>
                    <td className="border p-2">2018</td>
                    <td className="border p-2">9.2</td>
                  </tr>
                </tbody>
              </table>
              <h4 className="text-xl font-semibold mt-4 mb-2">Internships</h4>
              <ul className="list-disc pl-5 mb-4">
                <li>Successfully completed Cisco training on Cyber Security.</li>
                <li>Successfully completed Internship on frontend development in IBM (Online Course).</li>
                <li>Doing Full stack Java developer course in Naresh I Technologies in Hyderabad.</li>
              </ul>
              <h4 className="text-xl font-semibold mt-4 mb-2">Trainings Attended</h4>
              <ul className="list-disc pl-5 mb-4">
                <li>Successfully completed C/HTML/CSS/JavaScript training program at Naresh I Technologies, Hyderabad.</li>
                <li>Successfully completed Cisco training on Cyber Security.</li>
                <li>Successfully completed Java training in Internshala.</li>
              </ul>
              <h4 className="text-xl font-semibold mt-4 mb-2">Projects</h4>
              <ul className="list-disc pl-5 mb-4">
                <li>
                  <strong>Weather Report Application</strong>: Developed a full stack web application that provides real-time weather updates using Java Spring Boot for the backend and React for the frontend. Integrated with Open Weather Map API to fetch and display current weather conditions and forecasts.
                </li>
                <li>
                  <strong>Employee Leave Management System</strong>: Developed a comprehensive leave management system using Java for the backend and HTML, CSS, and JavaScript for the frontend, with Oracle as the database. Enabled efficient tracking, approval workflows, and reporting of employee leave data.
                </li>
                <li>
                  <strong>Oliva Skin & Care</strong>: A React-based app offering personalized skincare routines, a catalogue of olive-based products, and expert skincare tips. <a href="http://invroiceolivaclinic.com/" target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:underline">Link</a>
                </li>
                <li>
                  <strong>Carbia Motors</strong>: A frontend React app showcasing car listings, specifications, and dealer information. <a href="https://carbook-rho.vercel.app/main" target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:underline">Link</a>
                </li>
                <li>
                  <strong>Real Estate Explorer</strong>: A React-based front-end application for browsing and filtering property listings. <a href="https://frontend-virid-one-37.vercel.app/login" target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:underline">Link</a>
                </li>
              </ul>
              <h4 className="text-xl font-semibold mt-4 mb-2">Personal Details</h4>
              <p>Languages Known: English, Telugu, and Hindi</p>
              <h4 className="text-xl font-semibold mt-4 mb-2">Declaration</h4>
              <p>I hereby declare that the above written particulars are true to the best of my knowledge and belief.</p>
              <p className="mt-2">Syamsundar</p>
            </div>
            <button
              onClick={toggleResumeModal}
              className="mt-6 bg-black text-white p-3 rounded-lg hover:bg-gray-800 transition-all duration-300 w-full font-bold"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Certificate Modal */}
      {selectedCertificate && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 animate__animated animate__fadeIn">
          <div className="bg-white p-8 rounded-2xl max-w-3xl w-full max-h-[80vh] overflow-y-auto shadow-2xl animate__animated animate__zoomIn">
            <h2 className="text-3xl font-bold text-black mb-6">{selectedCertificate.name}</h2>
            <img
              src={selectedCertificate.image}
              alt={selectedCertificate.name}
              className="w-full h-auto object-contain rounded-lg mb-6"
            />
            <button
              onClick={closeCertificateModal}
              className="bg-black text-white p-3 rounded-lg hover:bg-gray-800 transition-all duration-300 w-full font-bold"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Home */}
      <section id="home" className="min-h-screen flex items-center justify-center bg-white relative overflow-hidden">
        <div className="absolute inset-0 bg-gray-200 opacity-30 animate__animated animate__pulse animate__infinite animate__slower"></div>
        <div className="container mx-auto flex flex-col md:flex-row items-center justify-between px-6 z-10 animate__animated animate__zoomIn">
          <div className="md:w-2/3 text-center md:text-left mb-12 md:mb-0">
            <h1 className="text-6xl font-black mb-6 text-black animate__animated animate__fadeInUp">
              Welcome to My Portfolio
            </h1>
            <p className="text-xl leading-relaxed text-gray-800 animate__animated animate__fadeInUp animate__delay-1s">
              I am a well-organized, energetic, and goal-oriented fresher, possessing positive attitude, problem-solving,
              and leadership skills with flair to explore suitable avenues in software development with good knowledge in
              Java, frontend development, C, and Oracle. To work in an organization that provides me with ample
              opportunities to enhance my skills and knowledge along with contributing to the growth of the organization.
            </p>
            <div className="mt-6 text-2xl font-semibold text-black animate__animated role-animation">
              {roles[currentRole]}
            </div>
          </div>
          <div className="md:w-1/3 flex justify-center">
            <img
              src="/syam.webp"
              alt="Panga Syamsundar Rao"
              className="w-80 h-80 rounded-full shadow-2xl object-cover border-4 border-black transform hover:scale-110 hover:rotate-6 transition-all duration-500 animate__animated animate__zoomInDown animate-border"
            />
          </div>
        </div>
      </section>

      {/* Skills */}
      <section id="skills" className={`py-20 bg-white ${activeSection === 'skills' ? 'animate__animated animate__lightSpeedInRight' : ''}`}>
        <div className="container mx-auto">
          <h2 className="text-5xl font-black text-center mb-10 text-black">Skills</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
            {skills.map(skill => (
              <div
                key={skill.id}
                className="p-6 bg-gray-100 rounded-2xl shadow-neumorphic hover:shadow-glow hover:-translate-y-2 transition-all duration-300 text-center font-semibold text-lg text-black animate__animated animate__bounceIn"
              >
                {skill.name}
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Projects */}
      <section id="projects" className={`py-20 bg-black text-white ${activeSection === 'projects' ? 'animate__animated animate__flipInX' : ''}`}>
        <div className="container mx-auto">
          <h2 className="text-5xl font-black text-center mb-10 text-white">Projects</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {projects.map(project => (
              <div
                key={project.id}
                className="p-6 bg-gray-800 rounded-2xl shadow-neumorphic hover:shadow-glow hover:-translate-y-2 transition-all duration-300 animate__animated animate__fadeInUp"
              >
                <h3 className="text-2xl font-bold text-white mb-2">{project.title}</h3>
                <p className="text-gray-300 mb-4">{project.description}</p>
                <a
                  href={project.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-white hover:text-gray-300 hover:underline transition-all duration-300 font-semibold animate__animated animate__pulse animate__infinite animate__slower"
                >
                  View Project
                </a>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Certificates */}
      <section id="certificates" className={`py-20 bg-white ${activeSection === 'certificates' ? 'animate__animated animate__lightSpeedInLeft' : ''}`}>
        <div className="container mx-auto">
          <h2 className="text-5xl font-black text-center mb-10 text-black">Certificates</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-2 gap-8">
            {certificates.map(cert => (
              <div
                key={cert.id}
                className="p-6 bg-gray-100 rounded-2xl shadow-neumorphic hover:shadow-glow hover:-translate-y-2 transition-all duration-300 animate__animated animate__bounceIn"
              >
                <h3 className="text-2xl font-bold text-black mb-2">{cert.name}</h3>
                <p className="text-base text-gray-800 mb-4">{cert.description}</p>
                <button
                  onClick={() => openCertificateModal(cert)}
                  className="bg-black text-white p-2 rounded-lg hover:bg-gray-800 transition-all duration-300 font-semibold animate__animated animate__pulse animate__infinite animate__slower"
                >
                  View Certificate
                </button>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Experience */}
      <section id="experience" className={`py-20 bg-black text-white ${activeSection === 'experience' ? 'animate__animated animate__flipInX' : ''}`}>
        <div className="container mx-auto">
          <h2 className="text-5xl font-black text-center mb-10 text-white">Experience</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <div className="p-6 bg-gray-800 rounded-2xl shadow-neumorphic hover:shadow-glow hover:-translate-y-2 transition-all duration-300 animate__animated animate__fadeInUp">
              <h3 className="text-2xl font-bold text-white mb-2">Full Stack Developer</h3>
              <p className="text-gray-300">
                <a
                  href="https://bluepal.com/"
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-white hover:text-gray-300 hover:underline"
                >
                  Bluepal
                </a> - Present
              </p>
              <p className="text-gray-300">Working as a Full Stack Developer, contributing to web application development using Java, React, and other modern technologies.</p>
            </div>
            <div className="p-6 bg-gray-800 rounded-2xl shadow-neumorphic hover:shadow-glow hover:-translate-y-2 transition-all duration-300 animate__animated animate__fadeInUp animate__delay-1s">
              <h3 className="text-2xl font-bold text-white mb-2">Full Stack Java Developer Course</h3>
              <p className="text-gray-300">Naresh I Technologies, Hyderabad</p>
              <p className="text-gray-300">I have completed a Full-Stack Development course during which I gained hands-on experience in both frontend and backend technologies. On the backend, I learned C, Core Java, Advanced Java, Spring Boot, Microservices, and Oracle. On the frontend, I worked with HTML, CSS, JavaScript, and React.js.</p>
            </div>
          </div>
        </div>
      </section>

      {/* Education */}
      <section id="education" className={`py-20 bg-white ${activeSection === 'education' ? 'animate__animated animate__lightSpeedInRight' : ''}`}>
        <div className="container mx-auto">
          <h2 className="text-5xl font-black text-center mb-10 text-black">Education</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <div className="p-6 bg-gray-100 rounded-2xl shadow-neumorphic hover:shadow-glow hover:-translate-y-2 transition-all duration-300 animate__animated animate__fadeInUp">
              <h3 className="text-2xl font-bold text-black mb-2">B.Tech in Computer Science & Engineering</h3>
              <p className="text-gray-800">CT University, Punjab - 2020 - 2024</p>
              <p className="text-gray-800">Graduated with a CGPA of 7.2, specializing in software development and computer science principles.</p>
            </div>
            <div className="p-6 bg-gray-100 rounded-2xl shadow-neumorphic hover:shadow-glow hover:-translate-y-2 transition-all duration-300 animate__animated animate__fadeInUp animate__delay-1s">
              <h3 className="text-2xl font-bold text-black mb-2">Intermediate (M.P.C)</h3>
              <p className="text-gray-800">Sri Chaitanya Junior College, Visakhapatnam - 2018-2020</p>
              <p className="text-gray-800">Completed with a CGPA of 8.7, with a focus on Mathematics, Physics, and Chemistry.</p>
            </div>
            <div className="p-6 bg-gray-100 rounded-2xl shadow-neumorphic hover:shadow-glow hover:-translate-y-2 transition-all duration-300 animate__animated animate__fadeInUp animate__delay-2s">
              <h3 className="text-2xl font-bold text-black mb-2">Secondary School Certificate (SSC)</h3>
              <p className="text-gray-800">Bradlaugh School - 2018</p>
              <p className="text-gray-800">Achieved a CGPA of 9.2, with a strong foundation in core academic subjects.</p>
            </div>
          </div>
        </div>
      </section>

      {/* Contact */}
      <section id="contact" className={`py-20 bg-black text-white ${activeSection === 'contact' ? 'animate__animated animate__flipInX' : ''}`}>
        <div className="container mx-auto">
          <h2 className="text-5xl font-black text-center mb-10 text-white">Contact Me</h2>
          <form onSubmit={handleContactSubmit} className="max-w-lg mx-auto bg-gray-800 p-8 rounded-2xl shadow-neumorphic hover:shadow-glow animate__animated animate__fadeInUp">
            <div className="mb-6">
              <label className="block text-lg mb-2 font-semibold text-white">Name</label>
              <input
                type="text"
                value={contactForm.name}
                onChange={e => setContactForm({ ...contactForm, name: e.target.value })}
                className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-gray-300 transition-all duration-300 transform hover:scale-[1.02] animate-border text-black"
                required
              />
            </div>
            <div className="mb-6">
              <label className="block text-lg mb-2 font-semibold text-white">Email</label>
              <input
                type="email"
                value={contactForm.email}
                onChange={e => setContactForm({ ...contactForm, email: e.target.value })}
                className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-gray-300 transition-all duration-300 transform hover:scale-[1.02] animate-border text-black"
                required
              />
            </div>
            <div className="mb-6">
              <label className="block text-lg mb-2 font-semibold text-white">Message</label>
              <textarea
                value={contactForm.message}
                onChange={e => setContactForm({ ...contactForm, message: e.target.value })}
                className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-gray-300 transition-all duration-300 transform hover:scale-[1.02] animate-border text-black"
                rows="5"
                required
              ></textarea>
            </div>
            <button
              type="submit"
              className="bg-white text-black p-3 rounded-lg hover:bg-gray-200 transition-all duration-300 w-full font-bold transform hover:scale-105 animate__animated animate__pulse animate__infinite animate__slower"
            >
              Send Message
            </button>
            {formStatus && <p className="mt-4 text-center text-gray-300 animate__animated animate__fadeIn">{formStatus}</p>}
          </form>
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-black bg-opacity-90 text-white p-4 fixed bottom-0 w-full z-20 shadow-2xl animate__animated animate__fadeInUp">
        <div className="container mx-auto flex justify-between items-center">
          <div className="flex space-x-8">
            <a
              href="https://www.linkedin.com/in/panga-syamsundar-rao-39b192226/"
              target="_blank"
              rel="noopener noreferrer"
              className="flex items-center space-x-2 hover:text-gray-300 transition-all duration-300 text-lg font-semibold transform hover:scale-110 animate__animated animate__pulse animate__infinite animate__slower"
            >
              <i className="fab fa-linkedin"></i>
              <span>LinkedIn</span>
            </a>
            <a
              href="https://www.instagram.com/syam_panga/"
              target="_blank"
              rel="noopener noreferrer"
              className="flex items-center space-x-2 hover:text-gray-300 transition-all duration-300 text-lg font-semibold transform hover:scale-110 animate__animated animate__pulse animate__infinite animate__slower"
            >
              <i className="fab fa-instagram"></i>
              <span>Instagram</span>
            </a>
            <a
              href="https://wa.me/918919004890"
              target="_blank"
              rel="noopener noreferrer"
