// App.js
import { useState, useEffect } from 'react';
import { Link } from 'react-scroll';
import axios from 'axios';
import 'animate.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './App.css';

function App() {
  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);
  const [contactForm, setContactForm] = useState({ name: '', email: '', message: '' });
  const [formStatus, setFormStatus] = useState('');
  const [activeSection, setActiveSection] = useState('');
  const [isResumeOpen, setIsResumeOpen] = useState(false);
  const [currentRole, setCurrentRole] = useState(0);
  const [selectedCertificate, setSelectedCertificate] = useState(null);

  const roles = ['Frontend Developer', 'Backend Developer', 'Full Stack Java Developer'];

  const certificates = [
    {
      id: 1,
      name: 'Cisco Cyber Security',
      image: '/Cisco Certificate.jpeg',
      description: 'Learned fundamentals of cybersecurity including network security, threat detection, and risk mitigation strategies.'
    },
    {
      id: 2,
      name: 'Android App Development',
      image: '/Android_App_Development.jpg',
      description: 'Covered Android development using Java and Android Studio with API integration and UI design.'
    },
    {
      id: 3,
      name: 'Frontend Development',
      image: '/IBM.jpg',
      description: 'Mastered HTML, CSS, JavaScript, and React.js with responsive design and UI development.'
    },
    {
      id: 4,
      name: 'Java',
      image: '/Java Certificate.png',
      description: 'Covered OOPs, multithreading, Spring Boot, and backend Java development.'
    },
    {
      id: 5,
      name: 'Full Stack Java Developer',
      image: '/Java Certificate.png',
      description: 'Built complete apps using Java, Spring Boot, MySQL, React. Hands-on with Git, Docker, Postman, Heroku, Vercel, JWT auth.'
    }
  ];

  const projectImages = [
    '/Weather.webp',
    '/Employee.png',
    '/olivaclinic_logo.jpg',
    '/car.png',
    '/Realestate.jpg'
  ];

  useEffect(() => {
    axios.get('http://localhost:5000/api/skills').then(res => setSkills(res.data));
    axios.get('http://localhost:5000/api/projects').then(res => setProjects(res.data));

    const roleInterval = setInterval(() => {
      setCurrentRole((prev) => (prev + 1) % roles.length);
    }, 2000);

    return () => clearInterval(roleInterval);
  }, []);

  const handleContactSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/contact', contactForm);
      setFormStatus('Message sent successfully!');
      setContactForm({ name: '', email: '', message: '' });
    } catch (error) {
      setFormStatus('Failed to send message.');
    }
  };

  const toggleResumeModal = () => setIsResumeOpen(!isResumeOpen);
  const openCertificateModal = (cert) => setSelectedCertificate(cert);
  const closeCertificateModal = () => setSelectedCertificate(null);

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-100 via-blue-100 to-pink-100 animate__animated animate__fadeIn font-sans">

      {/* Navbar */}
      <nav className="fixed top-0 w-full z-50 bg-gradient-to-r from-purple-800 via-blue-800 to-pink-700 text-white shadow-xl p-4 animate__animated animate__fadeInDown">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold animate__animated animate__bounceIn">Panga Syamsundar Rao</h1>
          <div className="flex space-x-4">
            {['About', 'Skills', 'Projects', 'Certificates', 'Experience', 'Education', 'Contact'].map((section, idx) => (
              <Link
                key={section}
                to={section.toLowerCase()}
                smooth={true}
                duration={500}
                spy={true}
                activeClass="text-yellow-300 border-b-2 border-yellow-300"
                onSetActive={() => setActiveSection(section.toLowerCase())}
                className={`cursor-pointer hover:text-yellow-300 transition duration-300 text-lg font-semibold animate__animated animate__fadeIn animate__delay-${idx + 1}s`}
              >
                {section}
              </Link>
            ))}
            <i
              className="fas fa-user-circle text-3xl cursor-pointer hover:scale-110 transition-transform animate__animated animate__zoomIn"
              onClick={toggleResumeModal}
            />
          </div>
        </div>
      </nav>

      {/* Resume Modal */}
      {isResumeOpen && (
        <div className="fixed inset-0 z-50 flex justify-center items-center bg-black bg-opacity-70 animate__animated animate__fadeIn">
          <div className="bg-white rounded-2xl p-8 w-11/12 max-w-4xl overflow-y-auto max-h-[80vh] shadow-xl animate__animated animate__zoomIn">
            <h2 className="text-2xl font-bold text-purple-800 mb-4">Resume</h2>
            <p className="text-gray-800 mb-2"><strong>Name:</strong> Panga Syamsundar Rao</p>
            <p className="text-gray-800 mb-2"><strong>Email:</strong> syampanga2003@gmail.com</p>
            <p className="text-gray-800 mb-2"><strong>Phone:</strong> +91-8919004890</p>
            <p className="text-gray-800 mb-4">
              I am a Full Stack Java Developer trained in Core Java, Spring Boot, React.js, MySQL, Docker, Git, JWT, and deployment tools like Heroku and Vercel. I have completed real-time projects like an E-Commerce app, Fitness Tracker, and Leave Management System.
            </p>
            <button onClick={toggleResumeModal} className="mt-4 w-full bg-purple-800 text-white py-2 rounded-lg hover:bg-purple-900 transition">
              Close
            </button>
          </div>
        </div>
      )}

      {/* Certificate Modal */}
      {selectedCertificate && (
        <div className="fixed inset-0 z-50 flex justify-center items-center bg-black bg-opacity-70 animate__animated animate__fadeIn">
          <div className="bg-white rounded-2xl p-6 w-11/12 max-w-2xl overflow-y-auto max-h-[80vh] shadow-xl animate__animated animate__zoomIn">
            <h2 className="text-xl font-bold text-purple-700 mb-4">{selectedCertificate.name}</h2>
            <img src={selectedCertificate.image} alt={selectedCertificate.name} className="rounded-xl mb-4 shadow-lg" />
            <p className="text-gray-700 mb-4">{selectedCertificate.description}</p>
            <button onClick={closeCertificateModal} className="w-full bg-purple-800 text-white py-2 rounded-lg hover:bg-purple-900 transition">
              Close
            </button>
          </div>
        </div>
      )}

      {/* About Section */}
      <section id="about" className="pt-24 pb-20 bg-gradient-to-br from-white via-purple-50 to-blue-50 text-center">
        <h1 className="text-4xl font-extrabold text-purple-800 animate__animated animate__fadeInUp">Welcome to My Portfolio</h1>
        <p className="text-xl mt-4 text-gray-700 animate__animated animate__fadeInUp animate__delay-1s">
          I’m a self-motivated fresher with hands-on experience in building modern web applications using Java, Spring Boot, React.js, and more.
        </p>
        <div className="mt-4 text-2xl text-purple-700 font-bold animate__animated animate__fadeIn animate__delay-2s">
          {roles[currentRole]}
        </div>
      </section>

      {/* Skills */}
      <section id="skills" className="py-20 bg-purple-100">
        <h2 className="text-3xl text-center font-bold text-purple-800 mb-10 animate__animated animate__lightSpeedInLeft">Skills</h2>
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 px-6">
          {skills.map(skill => (
            <div key={skill.id} className="bg-white rounded-xl shadow-lg p-4 text-center text-purple-800 font-semibold hover:scale-105 transition-all animate__animated animate__fadeInUp">
              {skill.name}
            </div>
          ))}
        </div>
      </section>

      {/* Projects */}
      <section id="projects" className="py-20 bg-gradient-to-br from-purple-800 via-blue-800 to-pink-700 text-white">
        <h2 className="text-3xl text-center font-bold mb-10 animate__animated animate__fadeInUp">Projects</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 px-6">
          {projects.map((project, idx) => (
            <div key={project.id} className="bg-white text-purple-900 rounded-2xl p-4 shadow-lg hover:scale-105 transition-all animate__animated animate__zoomIn">
              <h3 className="text-xl font-bold mb-2">{project.title}</h3>
              <img src={projectImages[idx]} alt={project.title} className="w-full h-40 object-cover rounded-lg mb-2" />
              <p className="text-sm mb-2">{project.description}</p>
              <a href={project.link} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:underline">View Project</a>
            </div>
          ))}
        </div>
      </section>

      {/* Certificates */}
      <section id="certificates" className="py-20 bg-blue-50 text-center">
        <h2 className="text-3xl font-bold text-purple-800 mb-10 animate__animated animate__lightSpeedInRight">Certificates</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 px-6">
          {certificates.map(cert => (
            <div key={cert.id} className="bg-white rounded-2xl p-4 shadow-lg hover:scale-105 transition-all animate__animated animate__zoomIn">
              <h3 className="text-xl font-semibold text-purple-700">{cert.name}</h3>
              <p className="text-gray-700 text-sm mt-2">{cert.description.substring(0, 100)}...</p>
              <button onClick={() => openCertificateModal(cert)} className="mt-3 bg-purple-700 text-white px-4 py-2 rounded-lg hover:bg-purple-800">
                View Certificate
              </button>
            </div>
          ))}
        </div>
      </section>

      {/* Contact */}
      <section id="contact" className="py-20 bg-purple-900 text-white text-center">
        <h2 className="text-3xl font-bold mb-6 animate__animated animate__fadeInUp">Contact Me</h2>
        <form onSubmit={handleContactSubmit} className="max-w-lg mx-auto bg-purple-800 p-6 rounded-xl shadow-lg">
          <input
            type="text"
            placeholder="Your Name"
            value={contactForm.name}
            onChange={e => setContactForm({ ...contactForm, name: e.target.value })}
            className="w-full p-3 mb-4 rounded-lg text-black"
            required
          />
          <input
            type="email"
            placeholder="Your Email"
            value={contactForm.email}
            onChange={e => setContactForm({ ...contactForm, email: e.target.value })}
            className="w-full p-3 mb-4 rounded-lg text-black"
            required
          />
          <textarea
            placeholder="Your Message"
            value={contactForm.message}
            onChange={e => setContactForm({ ...contactForm, message: e.target.value })}
            className="w-full p-3 mb-4 rounded-lg text-black"
            rows="4"
            required
          ></textarea>
          <button type="submit" className="w-full bg-yellow-400 text-purple-900 py-2 rounded-lg font-semibold hover:bg-yellow-500 transition">
            Send Message
          </button>
          {formStatus && <p className="mt-4 text-yellow-200">{formStatus}</p>}
        </form>
      </section>

      {/* Footer */}
      <footer className="bg-purple-800 text-white text-center p-4">
        <p className="text-sm">&copy; {new Date().getFullYear()} Developed by Syam | All Rights Reserved</p>
      </footer>
    </div>
  );
}

export default App;
