import { useState, useEffect } from 'react';
import { Link } from 'react-scroll';
import axios from 'axios';
import 'animate.css';
import './App.css';

function App() {
  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);
  const [certificates, setCertificates] = useState([]);
  const [contactForm, setContactForm] = useState({ name: '', email: '', message: '' });
  const [formStatus, setFormStatus] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/skills').then(res => setSkills(res.data));
    axios.get('http://localhost:5000/api/projects').then(res => setProjects(res.data));
    axios.get('http://localhost:5000/api/certificates').then(res => setCertificates(res.data));
  }, []);

  const handleContactSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/contact', contactForm);
      setFormStatus('Message sent successfully!');
      setContactForm({ name: '', email: '', message: '' });
    } catch (error) {
      setFormStatus('Failed to send message.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 via-blue-100 to-purple-200 animate__animated animate__fadeIn">
      {/* Navbar */}
      <nav className="bg-black bg-opacity-90 text-white p-4 fixed w-full top-0 z-10 shadow-2xl animate__animated animate__fadeInDown">
        <div className="container mx-auto flex justify-between items-center">
          <div className="text-2xl font-extrabold tracking-wide animate__animated animate__fadeInLeft">Panga Syamsundar Rao</div>
          <div className="space-x-4">
            {['Home', 'Skills', 'Projects', 'Certificates', 'Experience', 'About', 'Contact'].map((item, idx) => (
              <Link
                key={item}
                to={item.toLowerCase()}
                smooth={true}
                duration={500}
                className={`cursor-pointer hover:text-purple-300 transition-colors duration-300 font-medium animate__animated animate__fadeInDown animate__delay-${idx + 1}s`}
              >
                {item}
              </Link>
            ))}
          </div>
        </div>
      </nav>

      {/* Home */}
      <section id="home" className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-100 to-purple-200 animate__animated animate__fadeIn">
        <div className="container mx-auto flex flex-col md:flex-row items-center justify-between px-4 animate__animated animate__fadeIn">
          <div className="md:w-1/2 text-center md:text-left mb-8 md:mb-0">
            <h1 className="text-5xl font-extrabold mb-4 text-purple-800 animate__animated animate__fadeInUp">Welcome to My Portfolio</h1>
            <p className="text-lg leading-relaxed animate__animated animate__fadeInUp animate__delay-1s">
              I am a well-organized, energetic, and goal-oriented fresher, possessing positive attitude, problem-solving,
              and leadership skills with flair to explore suitable avenues in software development with good knowledge in
              Java, frontend development, C, and Oracle. To work in an organization that provides me with ample
              opportunities to enhance my skills and knowledge along with contributing to the growth of the organization.
            </p>
          </div>
          <div className="md:w-1/2 flex justify-center">
            <img
              src="/syam.webp"
              alt="Panga Syamsundar Rao"
              className="w-64 h-64 rounded-full shadow-2xl object-cover transform hover:scale-110 transition-transform duration-500 border-4 border-purple-300 animate__animated animate__zoomIn"
            />
          </div>
        </div>
      </section>

      {/* Skills */}
      <section id="skills" className="py-16 bg-white animate__animated animate__fadeInUp">
        <div className="container mx-auto">
          <h2 className="text-3xl font-extrabold text-center mb-8 text-purple-700">Skills</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {skills.map(skill => (
              <div
                key={skill.id}
                className="p-6 bg-gradient-to-br from-purple-100 to-blue-100 rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300 text-center font-semibold text-lg animate__animated animate__fadeInUp"
              >
                {skill.name}
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Projects */}
      <section id="projects" className="py-16 bg-gradient-to-br from-blue-100 to-purple-100 animate__animated animate__fadeInUp">
        <div className="container mx-auto">
          <h2 className="text-3xl font-extrabold text-center mb-8 text-purple-700">Projects</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {projects.map(project => (
              <div
                key={project.id}
                className="p-6 bg-white rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300 animate__animated animate__fadeInUp"
              >
                <h3 className="text-xl font-bold text-purple-800">{project.title}</h3>
                <p className="mb-2">{project.description}</p>
                <a
                  href={project.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-purple-700 hover:text-purple-900 hover:underline transition-colors duration-300 font-semibold"
                >
                  View Project
                </a>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Certificates */}
      <section id="certificates" className="py-16 bg-white animate__animated animate__fadeInUp">
        <div className="container mx-auto">
          <h2 className="text-3xl font-extrabold text-center mb-8 text-purple-700">Certificates</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {certificates.map(cert => (
              <div
                key={cert.id}
                className="p-6 bg-gradient-to-br from-purple-100 to-blue-100 rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300 animate__animated animate__fadeInUp"
              >
                <h3 className="text-xl font-bold text-purple-800">{cert.name}</h3>
                <p>{cert.issuer} - {cert.year}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Experience */}
      <section id="experience" className="py-16 bg-gradient-to-br from-blue-100 to-purple-100 animate__animated animate__fadeInUp">
        <div className="container mx-auto">
          <h2 className="text-3xl font-extrabold text-center mb-8 text-purple-700">Experience</h2>
          <div className="p-6 bg-white rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300 animate__animated animate__fadeInUp">
            <h3 className="text-xl font-bold text-purple-800">Full Stack Java Developer Course</h3>
            <p>Naresh I Technologies, Hyderabad - Ongoing</p>
            <p>Currently pursuing a comprehensive course in full-stack development with Java, React, and MySQL.</p>
          </div>
        </div>
      </section>

      {/* About */}
      <section id="about" className="py-16 bg-white animate__animated animate__fadeInUp">
        <div className="container mx-auto">
          <h2 className="text-3xl font-extrabold text-center mb-8 text-purple-700">About Me</h2>
          <p className="text-lg text-center max-w-2xl mx-auto">
            I am a B.Tech graduate in Computer Science & Engineering from CT University, Punjab (2020, CGPA: 7.2). I am
            passionate about software development, with expertise in Java, React, Spring Boot, and MySQL. I am a
            well-organized, energetic, and goal-oriented individual with strong problem-solving and leadership skills.
          </p>
        </div>
      </section>

      {/* Contact */}
      <section id="contact" className="py-16 bg-gradient-to-br from-blue-100 to-purple-100 animate__animated animate__fadeInUp">
        <div className="container mx-auto">
          <h2 className="text-3xl font-extrabold text-center mb-8 text-purple-700">Contact Me</h2>
          <form onSubmit={handleContactSubmit} className="max-w-lg mx-auto bg-white p-8 rounded-xl shadow-lg animate__animated animate__fadeInUp">
            <div className="mb-4">
              <label className="block text-lg mb-2 font-semibold">Name</label>
              <input
                type="text"
                value={contactForm.name}
                onChange={e => setContactForm({ ...contactForm, name: e.target.value })}
                className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-400 transition-all duration-300"
                required
              />
            </div>
            <div className="mb-4">
              <label className="block text-lg mb-2 font-semibold">Email</label>
              <input
                type="email"
                value={contactForm.email}
                onChange={e => setContactForm({ ...contactForm, email: e.target.value })}
                className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-400 transition-all duration-300"
                required
              />
            </div>
            <div className="mb-4">
              <label className="block text-lg mb-2 font-semibold">Message</label>
              <textarea
                value={contactForm.message}
                onChange={e => setContactForm({ ...contactForm, message: e.target.value })}
                className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-400 transition-all duration-300"
                rows="5"
                required
              ></textarea>
            </div>
            <button
              type="submit"
              className="bg-purple-700 text-white p-2 rounded-lg hover:bg-purple-900 transition-colors duration-300 w-full font-bold"
            >
              Send Message
            </button>
            {formStatus && <p className="mt-4 text-center text-purple-700">{formStatus}</p>}
          </form>
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-black text-white py-4 shadow-2xl animate__animated animate__fadeInUp">
        <div className="container mx-auto flex justify-center space-x-8">
          <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer" className="hover:text-purple-300 transition-colors duration-300 text-lg font-semibold">
            LinkedIn
          </a>
          <a href="https://instagram.com" target="_blank" rel="noopener noreferrer" className="hover:text-purple-300 transition-colors duration-300 text-lg font-semibold">
            Instagram
          </a>
          <a href="https://wa.me/918919004890" target="_blank" rel="noopener noreferrer" className="hover:text-purple-300 transition-colors duration-300 text-lg font-semibold">
            WhatsApp
          </a>
          <a href="https://naukri.com" target="_blank" rel="noopener noreferrer" className="hover:text-purple-300 transition-colors duration-300 text-lg font-semibold">
            Naukri
          </a>
        </div>
      </footer>
    </div>
  );
}

export default App;
